mapper.select:
SELECT COUNT(*)
FROM VOLUNTEER
WHERE STD_ID = #{stdId}

	AND INS_NAME LIKE '%' || #{keyword} || '%'


	AND VOL_ACT_CODE = #{code}


	AND VOL_ACT_CODE = #{code}
	AND INS_NAME LIKE '%' || #{keyword} || '%'

mapper.insert:

			SELECT MAX(NVL(TO_NUMBER(VOL_NUM), 0)) + 1 FROM VOLUNTEER

		INSERT INTO VOLUNTEER
		(
			VOL_NUM
           ,INS_NAME
           ,VOL_RECOG_TIME
           ,START_DATE
           ,END_DATE
           ,VOL_ACT_CONTENT
           ,VOL_ACT_CODE
           ,STD_ID
           ,SEM_CODE
           ,APPLY_DATE
           ,PROC_STAT_CODE
           ,DISAUTH_RSN
           ,FILE_GR_NUM
           ,YEAR
           ,AUTH_DOC_NUM
		)
		VALUES
		(
			#{volNum}
		   ,#{insName}
		   ,#{volRecogTime}
		   ,#{startDate}
		   ,#{endDate}
		   ,#{volActContenT}
		   ,#{volActCode}
		   ,#{stdId}
		   ,#{semCode}
		   ,sysdate
		   ,#{procStatCode}
		   ,#{disauthRsn, jdbcType=VARCHAR}
		   ,#{fileGrNum}
		   ,#{year}
		   ,#{authDocNum}

		)

mapper.update:
		UPDATE VOLUNTEER
		SET    INS_NAME = #{insName} 
			 , VOL_RECOG_TIME = #{volRecogTime}
			 , START_DATE = #{startDate}
			 , END_DATE = #{endDate}
			 , VOL_ACT_CONTENT = #{volActContenT} 
			 , VOL_ACT_CODE = #{volActCode}
			 , APPLY_DATE = SYSDATE
			 , PROC_STAT_CODE = #{procStatCode}
			 , DISAUTH_RSN = #{disauthRsn, jdbcType=VARCHAR}
			 , FILE_GR_NUM = #{fileGrNum, jdbcType=VARCHAR}
             , AUTH_DOC_NUM = #{authDocNum}
		WHERE  VOL_NUM = #{volNum}
